// src/components/Card.js
import React from 'react';
import './Card.css'; // Ensure to create this CSS file for styling
import axios from 'axios';

const Card=( { userPost } ) => {
    const handleDelete=async () => {
        try {
            const response=await axios.delete( `http://localhost:5000/posts/${ userPost._id }`, {
                withCredentials: true
            } );

            console.log( response.data ); // Optional: Log the delete response

            // Optionally, you can refresh the posts after deletion
            // Implement a function to update state or fetch posts again
        } catch ( error ) {
            console.error( 'Error deleting post:', error );
            // Handle error: show message, log, etc.
        }
    };

    return (
        <div className="card">
            <div className="card-header">
                <h4>{userPost.username}</h4>
                <button onClick={handleDelete} className="delete-button">
                    Delete
                </button>
            </div>
            <div className="card-content">
                <p>{userPost.userPost}</p>
            </div>
            <div className="card-actions">
                <button className="like-button">Like {userPost.likes}</button>
                <button className="dislike-button">Dislike {userPost.disLikes}</button>
            </div>
        </div>
    );
};

export default Card;
