import React, { useEffect, useState } from 'react';
import { baseurl } from '../url';
import axiosInstance from '../axiosConfig';
import './Friend3.css';
import FriendsNavbar from './FriendsNavbar';
import Loading from './Loading'; // Import the Loading component
import Navbar from './Navbar';

const Friend3=() => {
    const [allUsers, setAllUsers]=useState( [] );
    const [sentRequests, setSentRequests]=useState( new Set() );
    const [receivedRequests, setReceivedRequests]=useState( [] );
    const [loading, setLoading]=useState( true ); // Loading state
    const [error, setError]=useState( null ); // Error state

    useEffect( () => {
        const fetchAllUsers=async () => {
            try {
                const response=await axiosInstance.get( `${ baseurl }/friend/unkownuser`, {
                    withCredentials: true,
                } );
                const allUserData=response.data;
                setAllUsers( allUserData );
                setLoading( false ); // Data fetched successfully, stop loading
            } catch ( error ) {
                console.error( 'Error fetching all users:', error );
                setError( 'Error fetching users' ); // Set error message
                setLoading( false ); // Stop loading on error
            }
        };

        const fetchPendingRequests=async () => {
            try {
                const response=await axiosInstance.get( `${ baseurl }/friend/pendingReq`, {
                    withCredentials: true,
                } );
                const { sentRequests, receivedRequests }=response.data;

                setSentRequests( new Set( sentRequests.map( request => request.receiver._id ) ) );
                setReceivedRequests( receivedRequests );
            } catch ( error ) {
                console.error( 'Error fetching pending requests:', error );
                setError( 'Error fetching pending requests' ); // Set error message
            }
        };

        fetchAllUsers();
        fetchPendingRequests();
    }, [] );

    const sendFriendRequest=async ( userId ) => {
        try {
            const response=await axiosInstance.post(
                `${ baseurl }/friend/req/${ userId }`,
                {},
                { withCredentials: true }
            );

            setSentRequests( prevSentRequests => new Set( [...prevSentRequests, userId] ) );
        } catch ( error ) {
            console.error( 'Error sending friend request:', error );
            setError( 'Error sending friend request' ); // Set error message
        }
    };

    const acceptFriendRequest=async ( userId ) => {
        try {
            const response=await axiosInstance.post(
                `${ baseurl }/friend/pending/${ userId }`, // Assuming userId is the requestId
                {},
                { withCredentials: true }
            );
            console.log( 'Friend request accepted:', response.data );

            // Update the receivedRequests state to remove the accepted request
            setReceivedRequests( prev =>
                prev.filter( request => request._id!==userId )
            );
        } catch ( error ) {
            console.error( 'Error accepting friend request:', error );
            setError( 'Error accepting friend request' ); // Set error message
        }
    };


    const capitalizeFirstLetter=( string ) => {
        return string.charAt( 0 ).toUpperCase()+string.slice( 1 );
    };

    if ( loading ) {
        return <Loading />; // Use the Loading component
    }

    if ( error ) {
        return <div>{error}</div>; // Display error message
    }

    return (
        <div>
            <Navbar />
            <FriendsNavbar />
            <div className='container-friend3'>
                {allUsers.length>0? (
                    allUsers.map( user => (
                        <li key={user._id}>
                            {capitalizeFirstLetter( user.username )}
                            {receivedRequests.some( request => request.sender._id===user._id )&&(
                                <button
                                    className='accept-request-btn'
                                    onClick={() => acceptFriendRequest( user._id )} // Pass user._id as the requestId
                                >
                                    {console.log( user._id )}
                                    Accept Request
                                </button>
                            )}
                            <button
                                onClick={() => sendFriendRequest( user._id )}
                                className='button'
                                disabled={sentRequests.has( user._id )||receivedRequests.some( request => request.sender._id===user._id )}
                            >
                                {sentRequests.has( user._id )? 'Request Sent':( receivedRequests.some( request => request.sender._id===user._id )? 'Request Received':'Send Request' )}
                            </button>
                        </li>
                    ) )
                ):(
                    <div>No users found</div> // Display message if no users are found
                )}
            </div>
        </div>
    );
};

export default Friend3;
