import React, { useEffect, useState } from 'react'
import axios from 'axios';
import './Friend2.css'
import { baseurl } from '../url';


const Friend2=() => {
    const [friend, setAllFriends]=useState( [] );
    const [error, setError]=useState( '' );

    useEffect( () => {
        const fetchFriends=async () => {
            try {
                const response=await axios.get( `${ baseurl }/friend/all` );
                const friends=response.data;
                console.log( 'Friends:', friends );
                setAllFriends( friends );
            } catch ( error ) {
                console.error( 'Error fetching friends:', error );
                setError( 'Error fetching friends. Please try again.' ); // Update error state
            }
        };

        fetchFriends();
    }, [] ); // Empty dependency array ensures useEffect runs only once

    if ( error ) {
        return <div className="user-friends"><p>{error}</p></div>;
    }

    return (
        <div className='user-friends'>
            <h1>Logged In User's Friends</h1>
            <ul className='friends'>
                {friend.map( f => (
                    <li key={f._id}>{f.fName}</li>
                ) )}
            </ul>
        </div>
    );
};



export default Friend2
