import React, { useState, useCallback } from "react";
import { useNavigate } from 'react-router-dom';
import './login.css'; // Import the custom CSS

const Login=() => {
    const [isLoginMode, setIsLoginMode]=useState( true );

    const [formData, setFormData]=useState( {
        uname: '',
        userName: '',
        password: ''
    } );

    const handleInputChange=useCallback( ( e ) => {
        const { name, value }=e.target;
        setFormData( prevState => ( { ...prevState, [name]: value } ) );
    }, [] );

    const handleLogin=async () => {
        try {
            const response=await axios.post( 'http://localhost:5000/login', {
                email: formData.userName,
                password: formData.password
            } );
            console.log( response.data ); // Logged in successfully
            // Redirect or perform other actions based on response
        } catch ( error ) {
            console.error( error );
            // Handle error, e.g., display error message
        }
    };
    const navigate=useNavigate();

    const handleSubmit=( e ) => {
        e.preventDefault(); // Prevent the default form submission behavior
        console.log( "Form Submitted" );
        console.log( "Username:", formData.userName );
        console.log( "Password:", formData.password );
        // navigate('/timeline');
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h1>{isLoginMode? 'Login':'Signup'}</h1>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="username">Username</label>
                        <input
                            type="text"
                            id="username"
                            value={formData.userName}
                            onChange={handleInputChange}
                            name="userName"
                            required
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            value={formData.password}
                            onChange={handleInputChange}
                            name="password"
                            required
                        />
                    </div>
                    <button type="submit" className="btn-submit">
                        {isLoginMode? 'Login':'Signup'}
                    </button>
                </form>
                <p className="toggle-mode" onClick={() => setIsLoginMode( !isLoginMode )}>
                    {isLoginMode? 'New User? Sign Up':'Already have an account? Login'}
                </p>
            </div>
        </div>
    );
};

export default Login;
