import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Friend.css';

const FriendComponent=() => {
    const [allUsers, setAllUsers]=useState( [] );

    useEffect( () => {
        const fetchAllusers=async () => {
            try {
                const response=await axios.get( 'http://localhost:5000/user/all' )
                // console.log( response )
                console.log( response.data );

                setAllUsers( response.data )
            } catch ( error ) {
                console.error( 'Error fetching users:', error )
            }

        }

        fetchAllusers()

        function getStoredUserData() {
            const user=localStorage.getItem( 'user' );
            console.log( 'data from local storage', JSON.parse( user ) );
            return user? JSON.parse( user ):null;
        }


    }, [] )

    const handleClick=( e ) => {
        e.preventDefault();


    }

    function getStoredUserData() {
        const user=localStorage.getItem( 'user' ); // Or sessionStorage.getItem('user')
        console.log( "Friend.jsx", user )


        return user? JSON.parse( user ):null;
    }

    getStoredUserData();

    return (
        <div className="friend-container">
            <h1>All Users</h1>
            {allUsers.map( user =>
                <div key={user._id} className='user-card'>
                    <h2>{user.username}</h2>
                    <button onClick={handleClick}>Send</button>

                </div>
            )}

        </div>
    );
};

export default FriendComponent;
