import React, { useState, useEffect, useRef } from 'react';
import './navbar.css'; // Import custom CSS for styling
import { Link } from 'react-router-dom';
import logo from './notebook.gif'; // Import your logo image

import { baseurl } from '../url';
import axiosInstance from '../axiosConfig';

const Navbar=() => {
    const [menuOpen, setMenuOpen]=useState( false );

    const toggleMenu=() => {
        setMenuOpen( !menuOpen );
    };

    const closeMenu=() => {
        setMenuOpen( false );
    };

    // Handle clicks outside the menu to close it
    const menuRef=useRef( null );

    useEffect( () => {
        const handleClickOutside=( event ) => {
            if ( menuRef.current&&!menuRef.current.contains( event.target ) ) {
                setMenuOpen( false );
            }
        };

        document.addEventListener( 'mousedown', handleClickOutside );

        return () => {
            document.removeEventListener( 'mousedown', handleClickOutside );
        };
    }, [] );

    const handleLogout=async () => {
        try {
            await axiosInstance.post( `${ baseurl }/user/logout`, {
                withCredentials: true // Send cookies with the request
            } );
            localStorage.removeItem( 'user' ); // Remove user session data
            window.location.href='/login'; // Redirect to login page
        } catch ( error ) {
            console.error( 'Logout error:', error );
            // Handle error if needed
        }
    };

    return (
        <nav className="navbar">
            <div className="nav-header">
                <div className="nav-logo">
                    <Link to="/homepage">
                        <img src={logo} alt="Logo" /> {/* Use your logo image here */}
                    </Link>
                </div>
                <div className="nav-toggle" onClick={toggleMenu}>
                    <div className={`burger ${ menuOpen? 'open':'' }`}>
                        <div className="line"></div>
                        <div className="line"></div>
                        <div className="line"></div>
                    </div>
                </div>
            </div>
            <ul ref={menuRef} className={`nav-list ${ menuOpen? 'open':'' }`}>
                <li className="nav-item">
                    <Link to="/homepage" onClick={closeMenu}>Home</Link>
                </li>
                <li className="nav-item">
                    <Link to="/friendspost" onClick={closeMenu}>Timeline</Link>
                </li>
                <li className="nav-item">
                    <Link to="/friend" onClick={closeMenu}>Friends</Link>
                </li>
                <li className="nav-item">
                    <Link to="/about" onClick={closeMenu}>About</Link>
                </li>
                <li className="nav-item">
                    <button onClick={handleLogout}>Logout</button>
                </li>
            </ul>
        </nav>
    );
};

export default Navbar;
