import React, { useEffect, useState } from 'react';
import { baseurl } from '../url';
import axiosInstance from '../axiosConfig';
import './Friend3.css'



const Friend3=() => {

    const [allusers, setAllUsers]=useState( [] )
    const [loading, setLoading]=useState( false );


    useEffect( () => {
        const fetchAllUsers=async () => {
            try {
                const response=await axiosInstance.get( `${ baseurl }/friend/unkownuser`, {
                    withCredentials: true, // Include credentials in the request
                } );
                const allUserData=response.data;
                console.log( allUserData );
                setAllUsers( allUserData );
                setLoading( false );
            } catch ( error ) {
                console.error( 'Error fetching all users:', error );
                setLoading( false );
            }
        };

        fetchAllUsers();
    }, [] );


    const sendFriendRequest=async ( userId ) => {
        try {
            const response=await axiosInstance.post(
                `${ baseurl }/friend/req/${ userId }`,
                {},
                {
                    withCredentials: true, // Include credentials in the request
                }
            );
            console.log( response.daat )

        } catch ( error ) {

        }
    }




    return (
        <div>
            <div className='container-friend3'>
                {allusers.map( ( user ) => (
                    <li key={user._id}>
                        {user.username}

                        <button onClick={() => sendFriendRequest( user._id )} className='button'>Sending Req</button>
                    </li>
                ) )}


            </div>
        </div>
    )
}


export default Friend3;
