import React, { useEffect, useState } from 'react';
import './Friend.css';
import Navbar from './Navbar';
import { baseurl } from '../url';
import axiosInstance from '../axiosConfig';
import FriendsNavbar from './FriendsNavbar';
import Loading from './Loading'; // Assuming you have a Loading component

const FriendPage=() => {
    const [allUsers, setAllUsers]=useState( [] );
    const [sentRequests, setSentRequests]=useState( new Set() );
    const [receivedRequests, setReceivedRequests]=useState( [] );
    const [loading, setLoading]=useState( true );
    const [error, setError]=useState( null );

    useEffect( () => {
        const fetchAllUsers=async () => {
            try {
                const response=await axiosInstance.get( `${ baseurl }/friend/unknownuser`, {
                    withCredentials: true,
                } );
                setAllUsers( response.data );
                setLoading( false );
            } catch ( error ) {
                console.error( 'Error fetching all users:', error );
                setError( 'Error fetching users' );
                setLoading( false );
            }
        };

        const fetchPendingRequests=async () => {
            try {
                const response=await axiosInstance.get( `${ baseurl }/friend/pendingReq`, {
                    withCredentials: true,
                } );
                const { sentRequests, receivedRequests }=response.data;

                setSentRequests( new Set( sentRequests.map( request => request.receiver._id ) ) );
                setReceivedRequests( receivedRequests );
            } catch ( error ) {
                console.error( 'Error fetching pending requests:', error );
                setError( 'Error fetching pending requests' );
            }
        };

        fetchAllUsers();
        fetchPendingRequests();
    }, [] );

    const sendFriendRequest=async ( userId ) => {
        try {
            const response=await axiosInstance.post(
                `${ baseurl }/friend/req/${ userId }`,
                {},
                {
                    withCredentials: true,
                }
            );

            setSentRequests( prevSentRequests => new Set( [...prevSentRequests, userId] ) );
        } catch ( error ) {
            console.error( 'Error sending friend request:', error );
            setError( 'Error sending friend request' );
        }
    };

    const acceptFriendRequest=async ( requestId ) => {
        try {
            const response=await axiosInstance.post(
                `${ baseurl }/friend/pending/${ requestId }`,
                {},
                {
                    withCredentials: true,
                }
            );

            // Update receivedRequests state by filtering out the accepted request
            setReceivedRequests( prevRequests => prevRequests.filter( request => request._id!==requestId ) );
        } catch ( error ) {
            console.error( 'Error accepting friend request:', error );
            setError( 'Error accepting friend request' );
        }
    };

    const capitalizeFirstLetter=( string ) => {
        return string.charAt( 0 ).toUpperCase()+string.slice( 1 );
    };

    if ( loading ) {
        return <Loading />;
    }

    if ( error ) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <Navbar />
            <FriendsNavbar />
            <div className='friend-container'>
                <div className='content'>
                    <h2>All Users</h2>
                    <ul>
                        {allUsers.length>0? (
                            allUsers.map( user => (
                                <li key={user._id}>
                                    {capitalizeFirstLetter( user.username )}
                                    <button
                                        onClick={() => sendFriendRequest( user._id )}
                                        className='button'
                                        disabled={sentRequests.has( user._id )||receivedRequests.some( request => request.sender._id===user._id )}
                                    >
                                        {sentRequests.has( user._id )? 'Request Sent':'Send Request'}
                                    </button>
                                    {receivedRequests.some( request => request.sender._id===user._id )&&(
                                        <button
                                            className='accept-request-btn'
                                            onClick={() => acceptFriendRequest( receivedRequests.find( request => request.sender._id===user._id )._id )}
                                        >
                                            Accept Request
                                        </button>
                                    )}
                                </li>
                            ) )
                        ):(
                            <div>No users found</div>
                        )}
                    </ul>
                </div>
            </div>
        </div>
    );
};

export default FriendPage;
