import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Friend.css';

const FriendComponent=() => {
    const [allUsers, setAllUsers]=useState( [] );

    useEffect( () => {
        const fetchAllUsers=async () => {
            try {
                const response=await axios.get( 'http://localhost:5000/user/all', { withCredentials: true } );
                setAllUsers( response.data );
            } catch ( error ) {
                console.error( 'Error fetching users:', error );
            }
        };

        fetchAllUsers();
    }, [] );

    const capitalizeFirstLetter=( string ) => {
        return string.charAt( 0 ).toUpperCase()+string.slice( 1 );
    };

    const [pendingRequests, setPendingRequests]=useState( new Set() );

    const handleSendRequest=async ( userId ) => {
        if ( pendingRequests.has( userId ) ) {
            return; // Prevent duplicate requests
        }

        try {
            setPendingRequests( ( prevPendingRequests ) => new Set( prevPendingRequests ).add( userId ) );
            const response=await axios.post( `http://localhost:5000/friend/req/${ userId }`, null, { withCredentials: true } );
            console.log( response.data );
            // Handle success, update UI if needed
            setPendingRequests( ( prevPendingRequests ) => {
                const newPendingRequests=new Set( prevPendingRequests );
                newPendingRequests.delete( userId );
                return newPendingRequests;
            } );
        } catch ( error ) {
            console.error( 'Error sending friend request:', error );
            setPendingRequests( ( prevPendingRequests ) => {
                const newPendingRequests=new Set( prevPendingRequests );
                newPendingRequests.delete( userId );
                return newPendingRequests;
            } );
        }
    };

    const isPending=( userId ) => {
        return pendingRequests.has( userId );
    };

    return (
        <div className="friend-container">
            <h1>All Users</h1>
            <div className="user-list">
                {allUsers.map( ( user ) => (
                    <div key={user._id} className="user-card">
                        <h2>{capitalizeFirstLetter( user.username )}</h2>
                        <button
                            className={`send-request-btn ${ isPending( user._id )? 'pending':'' }`}
                            onClick={() => handleSendRequest( user._id )}
                            disabled={isPending( user._id )}
                        >
                            {isPending( user._id )? 'Pending':'Send Request'}
                        </button>
                    </div>
                ) )}
            </div>
        </div>
    );
};

export default FriendComponent;
