// src/Component/FriendPosts.js
import React, { useEffect, useState } from 'react';
import Card from './Card'; // Assuming you have a Card component for displaying posts
import axios from 'axios';
import './Card.css'; // Your CSS file for styling
import Navbar from './Navbar';
import { baseurl } from '../url';
import axiosInstance from '../axiosConfig';

axios.defaults.withCredentials=true;

const FriendPosts=() => {
    const [friendPosts, setFriendPosts]=useState( [] );
    const [loading, setLoading]=useState( true ); // State to manage loading

    useEffect( () => {
        const fetchFriendPosts=async () => {
            try {
                const response=await axiosInstance.get( `${ baseurl }/msg/displayFrndPost`, {
                    withCredentials: true
                } );
                setFriendPosts( response.data.friendPosts );
                setLoading( false ); // Set loading to false after data fetch
            } catch ( error ) {
                console.error( 'Error fetching friend posts:', error );
                setLoading( false ); // Handle error by setting loading to false
            }
        };

        fetchFriendPosts();
    }, [] );

    const formatDate=( dateString ) => {
        const options={ year: 'numeric', month: 'long', day: 'numeric' };
        const date=new Date( dateString );
        return date.toLocaleDateString( undefined, options );
    };

    if ( loading ) {
        // Display a loading animation or spinner while waiting for data
        return (
            <div className='card-container'>
                <Navbar />
                <div className="loading-spinner">
                    {/* Insert your loading animation component or spinner here */}
                    Loading...
                </div>
            </div>
        );
    }

    return (
        <div className='card-container'>
            <Navbar />

            {friendPosts.length===0? (
                <p>No friend posts available.</p>
            ):(
                friendPosts.map( post => (
                    post.messagesList.map( message => (
                        <Card
                            key={message._id}
                            username={post.username.toUpperCase()} // Assuming username is available in each post object
                            userPost={message.userPost}
                            likes={message.likes}
                            disLikes={message.disLikes}
                            date={formatDate( message.date )}
                        />
                    ) )
                ) )
            )}
        </div>
    );
};

export default FriendPosts;
