import React, { useEffect, useState } from 'react';
import Card from './Card';
import './Timeline.css';
import Navbar from './Navbar';
import { baseurl } from '../url';
import axiosInstance from '../axiosConfig';

const About=() => {
    const [posts, setPosts]=useState( [] );

    useEffect( () => {
        const fetchUserPosts=async () => {
            try {
                const response=await axiosInstance.get( `${ baseurl }/msg/all`, {
                    withCredentials: true // Ensure credentials are included
                } );
                console.log( response );
                // Assuming response.data is an array of user objects with messagesList array
                const allPosts=response.data.reduce( ( accumulator, currentUser ) => {
                    if ( currentUser.messagesList&&Array.isArray( currentUser.messagesList ) ) {
                        accumulator.push( ...currentUser.messagesList );
                    }
                    return accumulator;
                }, [] );
                setPosts( allPosts );
            } catch ( error ) {
                console.error( 'Error while fetching posts', error );
            }
        };

        fetchUserPosts();
    }, [] ); // Empty dependency array ensures useEffect runs only once

    return (
        <div className="card-container">
            <Navbar />
            <h2>Logged User's Posts Only</h2>
            {posts.map( post => (
                <Card
                    key={post._id} // Assuming _id is a unique identifier for each post
                    userPost={post}
                />
            ) )}
        </div>
    );
};

export default About;
