import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Friend.css';

const FriendComponent=() => {
    const [session, setSession]=useState( null ); // Use null for initial state
    const [allUsers, setAllUsers]=useState( [] ); // To store all users
    const [receivedRequests, setReceivedRequests]=useState( [] ); // To store received friend requests
    const [sentRequests, setSentRequests]=useState( new Set() ); // To track sent friend requests
    const [loading, setLoading]=useState( true ); // To manage loading state

    useEffect( () => {
        const fetchLoginUserId=async () => {
            try {
                const response=await axios.get( 'http://localhost:5000/user/session', {
                    withCredentials: true // Include credentials in the request
                } );
                const data=response.data;
                console.log( data );
                setSession( data );
            } catch ( error ) {
                console.error( 'Error fetching session data:', error );
            }
        };

        fetchLoginUserId();
    }, [] );

    useEffect( () => {
        const fetchAllUsers=async () => {
            try {
                const response=await axios.get( 'http://localhost:5000/user/all', {
                    withCredentials: true // Include credentials in the request
                } );
                const allUserData=response.data;
                console.log( allUserData );
                setAllUsers( allUserData );
                setLoading( false );
            } catch ( error ) {
                console.error( 'Error fetching all users:', error );
                setLoading( false );
            }
        };

        fetchAllUsers();
    }, [] );

    useEffect( () => {
        const fetchReceivedRequests=async () => {
            try {
                if ( session ) {
                    const response=await axios.get( 'http://localhost:5000/friend/recievedReq', {
                        withCredentials: true
                    } );
                    const requests=response.data;
                    console.log( 'Received Requests:', requests );
                    setReceivedRequests( requests );
                }
            } catch ( error ) {
                console.error( 'Error fetching received friend requests:', error );
            }
        };

        fetchReceivedRequests();
    }, [session] );

    const sendFriendRequest=async ( userId ) => {
        try {
            if ( session ) {
                const response=await axios.post( `http://localhost:5000/friend/req/${ userId }`, {}, {
                    withCredentials: true // Include credentials in the request
                } );
                console.log( 'Friend request sent:', response.data );
                setSentRequests( ( prev ) => new Set( prev ).add( userId ) );
            } else {
                console.error( 'User session not found' );
            }
        } catch ( error ) {
            console.error( 'Error sending friend request:', error );
        }
    };

    const acceptFriendRequest=async ( requestId ) => {
        try {
            const response=await axios.post( `http://localhost:5000/friend/accept/${ requestId }`, {}, {
                withCredentials: true
            } );
            console.log( 'Friend request accepted:', response.data );
            setReceivedRequests( ( prev ) => prev.filter( request => request._id!==requestId ) );
        } catch ( error ) {
            console.error( 'Error accepting friend request:', error );
        }
    };

    if ( loading ) {
        return <p>Loading...</p>;
    }

    return (
        <div className="friend-container">
            {session? (
                <div>
                    <h1>Welcome, {session.name}</h1>
                    <p>User ID: {session.Userid}</p>
                </div>
            ):(
                <p>Loading session...</p>
            )}
            <h2>All Users</h2>
            <ul>
                {allUsers.map( user => (
                    <li key={user._id}>
                        {user.name} ({user.email})
                        <button
                            onClick={() => sendFriendRequest( user._id )}
                            disabled={sentRequests.has( user._id )}
                        >
                            {sentRequests.has( user._id )? 'Request Sent':'Send Friend Request'}
                        </button>
                    </li>
                ) )}
            </ul>
            <h2>Received Friend Requests</h2>
            <ul>
                {receivedRequests.map( request => (
                    <li key={request._id}>
                        Request from: {request.senderName} ({request.senderEmail})
                        <button onClick={() => acceptFriendRequest( request._id )}>
                            Accept Request
                        </button>
                    </li>
                ) )}
            </ul>
        </div>
    );
};

export default FriendComponent;
