import React, { useState, useCallback } from "react";
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './login.css'; // Import the custom CSS

const Login=() => {
    const [isLoginMode, setIsLoginMode]=useState( true );
    const [formData, setFormData]=useState( {
        username: '',
        email: '',
        password: ''
    } );

    const handleInputChange=useCallback( ( e ) => {
        const { name, value }=e.target;
        setFormData( prevState => ( { ...prevState, [name]: value } ) );
    }, [] );

    const handleLogin=async () => {
        try {
            const response=await axios.post( 'http://localhost:5000/login', {

                email: formData.email,
                password: formData.password
            } );
            console.log( response.data ); // Logged in successfully
            // Redirect or perform other actions based on response
        } catch ( error ) {
            console.error( error );
            // Handle error, e.g., display error message
        }
    };

    const navigate=useNavigate();

    const handleSubmit=( e ) => {
        e.preventDefault(); // Prevent the default form submission behavior
        console.log( "Form Submitted" );
        console.log( "Username:", formData.username );
        console.log( "Email:", formData.email );
        console.log( "Password:", formData.password );
        if ( isLoginMode ) {
            handleLogin();
        } else {
            // Handle signup here
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h1>{isLoginMode? 'Login':'Signup'}</h1>
                <form onSubmit={handleSubmit}>
                    {!isLoginMode&&(
                        <div className="form-group">
                            <label htmlFor="username">Username</label>
                            <input
                                type="text"
                                id="username"
                                value={formData.username}
                                onChange={handleInputChange}
                                name="username"
                                required
                            />
                        </div>
                    )}

                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            value={formData.email}
                            onChange={handleInputChange}
                            name="email"
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            id="password"
                            value={formData.password}
                            onChange={handleInputChange}
                            name="password"
                            required
                        />
                    </div>
                    <button type="submit" className="btn-submit">
                        {isLoginMode? 'Login':'Signup'}
                    </button>
                </form>
                <p className="toggle-mode" onClick={() => setIsLoginMode( !isLoginMode )}>
                    {isLoginMode? 'New User? Sign Up':'Already have an account? Login'}
                </p>
            </div>
        </div>
    );
};

export default Login;
