import React, { useEffect, useState } from 'react';
import Card from './Card';
import './Timeline.css';
import axios from 'axios';
axios.defaults.withCredentials=true;

const Timeline=() => {
    const [posts, setPosts]=useState( [] );

    useEffect( () => {
        const cardData=async () => {
            try {
                const response=await axios.get( 'http://localhost:5000/msg/allUserspost', {
                    withCredentials: true
                } );
                console.log( response );
                setPosts( response.data );
            } catch ( error ) {
                console.error( 'Error while fetching request', error );
            }
        };

        cardData();
    }, [] );

    return (
        <div className='card-container'>
            {posts.map( post => (
                <Card
                    key={post._id}
                    username={post.username}
                    userPost={post.messagesList[0].userPost}
                    likes={post.messagesList[0].likes}
                    disLikes={post.messagesList[0].disLikes}
                />
            ) )}
        </div>
    );
};

export default Timeline;
