import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Friend.css';

const FriendComponent=() => {
    const [allUsers, setAllUsers]=useState( [] );

    useEffect( () => {
        const fetchAllusers=async () => {
            try {
                const response=await axios.get( 'http://localhost:5000/user/all' )
                console.log( response.data );

                setAllUsers( response.data )
            } catch ( error ) {
                console.error( 'Error fetching users:', error )
            }

        }

        fetchAllusers()

    }, [setAllUsers] )


    return (
        <div className="friend-container">
            <h1>All Users</h1>
            <div className="user-list">
                {allUsers.map( ( user ) => (
                    <div key={user._id} className="user-card">
                        <h2>{capitalizeFirstLetter( user.username )}</h2>
                        <button
                            className={`send-request-btn ${ friendList.has( user._id )? 'already-friend':( pendingRequests.has( user._id )? 'pending':'' ) }`}
                            onClick={() => handleSendRequest( user._id )}
                            disabled={pendingRequests.has( user._id )||friendList.has( user._id )}
                        >
                            {friendList.has( user._id )? 'Already Friend':( pendingRequests.has( user._id )? 'Pending':'Send Request' )}
                        </button>
                    </div>
                ) )}
            </div>
        </div>
    );
};

export default FriendComponent;
