// src/Component/Timeline.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from './Card';
import './Timeline.css';

const Timeline=() => {
    const [posts, setPosts]=useState( [] );

    useEffect( () => {
        const fetchData=async () => {
            try {
                const response=await axios.get( 'http://localhost:5000/msg/allUserspost' );
                setPosts( response.data ); // Assuming response.data contains the array of users with messagesList
            } catch ( error ) {
                console.error( 'Error fetching the posts:', error );
            }
        };

        fetchData();
    }, [] );

    return (
        <div className='card-container'>
            {posts.map( user =>
                user.messagesList.map( ( message, index ) => (
                    <Card
                        key={`${ user._id }-${ index }`}
                        username={user.username}
                        userPost={message.userPost}
                        likes={message.likes}
                        disLikes={message.disLikes}
                    />
                ) )
            )}
        </div>
    );
};

export default Timeline;
