import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Friend.css'
const FriendComponent=() => {
    const [allUsers, setAllUsers]=useState( [] );
    const [allFriends, setAllFriends]=useState( [] );
    const [pendingRequests, setPendingRequests]=useState( [] );

    // Fetch all users
    useEffect( () => {
        const fetchAllUsers=async () => {
            try {
                const response=await axios.get( 'http://localhost:5000/user/all', { withCredentials: true } );
                setAllUsers( response.data );
            } catch ( error ) {
                console.error( 'Error fetching users:', error );
            }
        };

        fetchAllUsers();
    }, [] );

    // Fetch all friends and pending requests
    useEffect( () => {
        const fetchAllFriendsAndRequests=async () => {
            try {
                const friendsResponse=await axios.get( 'http://localhost:5000/friend/all', { withCredentials: true } );
                const pendingResponse=await axios.get( 'http://localhost:5000/friend/pending', { withCredentials: true } );

                setAllFriends( friendsResponse.data.friends );
                setPendingRequests( pendingResponse.data.requests );
            } catch ( error ) {
                console.error( 'Error fetching friends or pending requests:', error );
            }
        };

        fetchAllFriendsAndRequests();
    }, [] );

    // Function to send friend request
    // const handleSendRequest=async ( userId ) => {
    //     try {
    //         const response=await axios.post( `http://localhost:5000/friend/req/${ userId }`, null, { withCredentials: true } );
    //         console.log( 'Friend request sent:', response.data );
    //         // You may update state or UI as needed after sending the request
    //     } catch ( error ) {
    //         console.error( 'Error sending friend request:', error );
    //     }
    // };

    // // Function to accept pending friend request
    // const handleAcceptRequest=async ( requestId ) => {
    //     try {
    //         const response=await axios.put( `http://localhost:5000/friend/accept/${ requestId }`, null, { withCredentials: true } );
    //         console.log( 'Friend request accepted:', response.data );
    //         // You may update state or UI as needed after accepting the request
    //     } catch ( error ) {
    //         console.error( 'Error accepting friend request:', error );
    //     }
    // };

    // // Function to check if a user is already a friend
    // const isFriend=( userId ) => {
    //     return allFriends.some( ( friend ) => friend._id===userId );
    // };

    // // Function to check if a user has pending request
    // const isPendingRequest=( userId ) => {
    //     return pendingRequests.some( ( request ) => request.sender===userId );
    // };

    return (
        <div className="friend-container">
            <h1>Friend Management</h1>

            <div className="all-users">
                <h2>All Users</h2>
                <ul>
                    {allUsers.map( ( user ) => (
                        <li key={user._id}>
                            {user.username}
                            {!isFriend( user._id )&&!isPendingRequest( user._id )&&(
                                <button onClick={() => handleSendRequest( user._id )}>Send Friend Request</button>
                            )}
                            {isPendingRequest( user._id )&&<span>Request Pending</span>}
                        </li>
                    ) )}
                </ul>
            </div>

            <div className="friends-list">
                <h2>Friends</h2>
                <ul>
                    {allFriends.map( ( friend ) => (
                        <li key={friend._id}>
                            {friend.username}
                        </li>
                    ) )}
                </ul>
            </div>

            <div className="pending-requests">
                <h2>Pending Requests</h2>
                <ul>
                    {pendingRequests.map( ( request ) => (
                        <li key={request._id}>
                            {request.senderUsername}
                            {!isFriend( request.sender )&&(
                                <button onClick={() => handleAcceptRequest( request._id )}>Accept Request</button>
                            )}
                        </li>
                    ) )}
                </ul>
            </div>
        </div>
    );
};

export default FriendComponent;
