import React, { useEffect, useState } from 'react';
import { baseurl } from '../url';
import axiosInstance from '../axiosConfig';
import './Friend3.css';

const Friend3=() => {
    const [allUsers, setAllUsers]=useState( [] );
    const [sentRequests, setSentRequests]=useState( new Set() );
    const [receivedRequests, setReceivedRequests]=useState( new Set() );

    useEffect( () => {
        const fetchAllUsers=async () => {
            try {
                const response=await axiosInstance.get( `${ baseurl }/friend/unknownuser`, {
                    withCredentials: true,
                } );
                const allUserData=response.data;
                console.log( allUserData );
                setAllUsers( allUserData );
            } catch ( error ) {
                console.error( 'Error fetching all users:', error );
            }
        };

        const fetchPendingRequests=async () => {
            try {
                const response=await axiosInstance.get( `${ baseurl }/friend/pendingReq`, {
                    withCredentials: true,
                } );
                const { sentRequests, receivedRequests }=response.data;
                console.log( sentRequests );
                console.log( receivedRequests );

                // Convert arrays to sets for state management
                setSentRequests( new Set( sentRequests.map( request => request.receiver._id ) ) );
                setReceivedRequests( new Set( receivedRequests.map( request => request.sender._id ) ) );
            } catch ( error ) {
                console.error( 'Error fetching pending requests:', error );
            }
        };

        fetchAllUsers();
        fetchPendingRequests();
    }, [] );

    const sendFriendRequest=async ( userId ) => {
        try {
            const response=await axiosInstance.post(
                `${ baseurl }/friend/req/${ userId }`,
                {},
                {
                    withCredentials: true,
                }
            );
            console.log( response.data );

            // Update sentRequests immediately after sending request
            setSentRequests( prev => new Set( [...prev, userId] ) );
        } catch ( error ) {
            console.error( 'Error sending friend request:', error );
        }
    };

    const capitalizeFirstLetter=( string ) => {
        return string.charAt( 0 ).toUpperCase()+string.slice( 1 );
    };

    return (
        <div>
            <div className='container-friend3'>
                {allUsers.map( user => (
                    <li key={user._id}>
                        {capitalizeFirstLetter( user.username )}
                        <button
                            onClick={() => sendFriendRequest( user._id )}
                            className='button'
                            disabled={sentRequests.has( user._id )||receivedRequests.has( user._id )}
                        >
                            {sentRequests.has( user._id )? 'Request Sent':( receivedRequests.has( user._id )? 'Request Received':'Send Request' )}
                        </button>
                    </li>
                ) )}
            </div>
        </div>
    );
};

export default Friend3;
