// src/Component/Timeline.js
import React, { useEffect, useState } from 'react';
import Card from './Card';
import './Timeline.css';
import axios from 'axios';
axios.defaults.withCredentials=true;

const Timeline=() => {
    const [posts, setPosts]=useState( [] );

    useEffect( () => {
        const cardData=async () => {
            try {
                // Ensure withCredentials is set to true to send cookies with the request
                const response=await axios.get( 'http://localhost:5000/msg/allUserspost', {
                    withCredentials: true
                } );
                console.log( response );
                setPosts( response.data );
            } catch ( error ) {
                console.error( 'Error while fetching request', error );
            }
        };

        cardData();
    }, [] );

    return (
        <div className='card-container'>
            {posts.map( post => (
                post.messagesList.map( message => (
                    <Card
                        key={message._id}
                        username={post.username}
                        userPost={message.userPost}
                        likes={message.likes}
                        disLikes={message.disLikes}
                    />
                ) )
            ) )}
        </div>
    );
};

export default Timeline;
